import React, { useState } from 'react';
import './SearchBar.css';
import Dropdown from './Dropdown.js';

const SearchBar = ({ onFormSubmit }) => {

    const [term, setTerm] = useState('');
    const [selectedOption, setSelectedOption] = useState(options[0]);

    const onSubmit = (event) => {
        event.preventDefault();

        onFormSubmit(term, selectedOption);
    };

    // const onValueChange = ( option ) =>{
    //   setSelectedOption(option);
    // };

    const options = [
{
  label: 'The Color Red',
  value: 'Red'
},
{
  label: 'The Color Green',
  value: 'green'
},
{
  label: 'A Shade of Blue',
  value: 'blue'
}
];
    return (

        <div className="search-bar ui segment">
        <form onSubmit={onSubmit} className="ui form">
          <div className="field">
            <label>Search for Lego Set</label>
            <input className="btn btn-default column submit-btn" value ='submit' type="submit" />
            <div className="search-bar">
              <input
                className='search-bar-text four wide column'
                type="text"
                value={term}
                onChange={(event) => setTerm(event.target.value)}
              />
            </div>
          {/*<label>Submit</label>*/}
          <Dropdown
            label="Select a Color"
            options={options}
            selected={selectedOption}
            onSelectedChange={setSelectedOption}
          />
          <div className="radiobuttongroup ui grid"> {/*start of radio buttons Option*/}
            <div className="radio three wide column">
            <label>
              <input
                type="radio"
                value="TITLE"
                checked={selectedOption === "TITLE"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Title
            </label>
          </div>
          <div className="radio three wide column">
            <label>
              <input
                type="radio"
                value="NUMBER"
                checked={selectedOption === "NUMBER"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Number
            </label>
          </div>
          <div className="radio three wide column">
            <label>
              <input
                type="radio"
                value="THEME"
                checked={selectedOption === "THEME"}
                onChange={(event) => setSelectedOption(event.target.value)}
              />
              &nbsp;Theme
            </label>
          </div>
        </div> {/*end of options*/}
        </div>
        </form>
      </div>
    );

};



export default SearchBar;
